import React, { useState, useEffect, setState } from 'react';
import Editor from './Editor'

function Codepen() {

const [html, setHtml] = useState('')
const [css, setCss] = useState('')
const [js, setJs] = useState('')
const [srcDoc, setSrcDoc] = useState('')
const [accessToken, setAccessToken] = useState('')
const [refreshToken, setRefreshToken] = useState('')
const [loginToken, setLoginToken] = useState('')



useEffect(() => {
  const timeout = setTimeout(() => {
    setSrcDoc(`
      <html>
        <body>${html}</body>
        <style>${css}</style>
        <script>${js}</script>
      </html>
    `)
  }, 250)

  return () => clearTimeout(timeout)
}, [html, css, js])

function getRefreshToken(){
  fetch('refresh_token')
    .then(response => response.json())
    .then(data => {
      setRefreshToken(data)
    });
}
function getAccessToken(){
  fetch('access_token?refresh_token='+refreshToken)
    .then(response => response.json())
    .then(data => {
      accessToken(data)
    });
}
function getLoginToken(){
  fetch('login_token?access_token='+accessToken)
    .then(response => response.json())
    .then(data => {
      accessToken(data)
    });
}
function loadDash(){
  setHtml(`
<div id="YellowfinDiv"></div>
<script src="http://localhost:8080/JsAPI/v3"></script>
<script>
window.yellowfin.loadDashboardAPI().then(() => { 

window.yellowfin.dashboards.loadDashboard({ 
  dashboardUUID: "ae52f8f8-fe7e-42a1-a154-1e89644aa2a9", 
  element:  document.querySelector('#YellowfinDiv'),
            showShare: false,
}).then(dashboard => { 	});
})
</script>
    `);

}

function loadReport(){
  setHtml(`
<div id="YellowfinDiv"></div>
<script src="http://localhost:8080/JsAPI/v3"></script>
<script>
yellowfin.loadReport({
element: document.querySelector("#YellowfinDiv"),
reportUUID: "5d35f38b-8694-499c-baad-cf49067d997e",
});
</script>
  `)
}

return (
  <div id="container">
    <div className="pane nav">
      <div className="navOption" onClick={getRefreshToken}>Refresh Token</div>
      <div className="navOption" onClick={loadDash}>JS-API Dashboard</div>
      <div className="navOption" onClick={loadReport}>JS-API Report</div>
    </div>
    <div className="pane top-pane">
      <Editor
        language="xml"
        displayName="HTML"
        value={html}
        onChange={setHtml}
        className="HtmlContainer"
      />
    </div>
    <div className="pane script-pane">
      <iframe
        srcDoc={srcDoc}
        title="output"
        sandbox="allow-same-origin allow-scripts"
        frameBorder="0"
        width="100%"
        height="100%"
      />
    </div>
  </div>
)
}
export default Codepen;
